CREATE TEMP TABLE table_name (a, b)
WITH (USER_CATALOG_TABLE)
ON COMMIT DROP
TABLESPACE tablespace_name
AS
SELECT *
  FROM
      foo;
CREATE TEMP TABLE table_name2
WITH (OIDS)
AS
SELECT *
  FROM
      foo
WITH NO DATA;
CREATE UNLOGGED TABLE table_name3
WITH (OIDS= FALSE)
ON COMMIT DELETE ROWS
AS
SELECT *
  FROM
      foo;
