CREATE TABLE intervals_table(
    i_1 interval YEAR,
    i_2 interval MONTH,
    i_3 interval DAY,
    i_4 interval HOUR,
    i_5 interval MINUTE,
    i_6 interval SECOND,
    i_7 interval YEAR TO MONTH,
    i_8 interval DAY TO HOUR,
    i_9 interval DAY TO MINUTE,
    i_10 interval DAY TO SECOND,
    i_11 interval HOUR TO MINUTE,
    i_12 interval HOUR TO SECOND,
    i_13 interval MINUTE TO SECOND,
    i_14 interval (2)
);
